package kr.ac.konkuk.controller;
import org.apache.commons.codec.binary.Base64;
public class Base64Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* base64 encoding */
		String text="data:image/png;base64,";
		
				
		byte[] encoded = Base64.encodeBase64(text.getBytes());
		 
		/* base64 decoding */
		byte[] decoded = Base64.decodeBase64(encoded);
		byte[] decoded2=Base64.decodeBase64(text.getBytes());
		
		 
		//System.out.println("인코딩 전 : " + text);
		System.out.println("인코딩 text : " + new String(encoded));
		System.out.println("디코딩 text : " + new String(decoded));
		System.out.println("디코딩2 text : " + new String(decoded2));
	}

}
